import React, { Component } from "react";
import axios from "axios";
import { MDBContainer, MDBRow, MDBCol, MDBInput} from 'mdbreact';


const language = [
  // English
  {
    'welcome': 'Welcome',
    'description': 'This app demonstrates how to easily use a multilanguage mechanism with Office UI Fabric',
  },
  // Deutsch
  {
    'welcome': 'Willkommen',
    'description': 'Diese App demonstriert, wie man leicht einen mehrsprachigen Mechanismus mit Office UI Fabric verwendet',
  },
  // Italian
  {
    'welcome': 'Benvenuto',
    'description': 'Questa app dimostra come utilizzare facilmente un meccanismo multilingue con Office UI Fabric',
  },
  // Spanish
  {
    'welcome': 'Bienvenido',
    'description': 'Esta aplicación demuestra cómo utilizar fácilmente un mecanismo multilenguaje con Office UI Fabric',
  }
]


axios.defaults.withCredentials = true;
class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      password: "",
      email: "",
      error: null,
      status: "",
    currentLanguage: "",
      valerrors: null
    };
    this.changeHandler = this.changeHandler.bind(this);
    this.submitHandler = this.submitHandler.bind(this);
  }

  changeHandler(e) {
    this.setState({
      [e.target.name]: e.target.value
    });
  }
  submitHandler(e) {
    e.preventDefault();
    axios
      .post("http://localhost:4000/api/login", this.state)
      .then(res => {
        if (res.data.error) {
          return this.setState({ error: res.data.message });
        }
        if (res.data.errors) {
          return this.setState({ valerrors: res.data.errors });
        }
        return (window.location = "/filelist");
      });
  }
  render() {
    return (
      <MDBContainer>
       <p class="h3 mb-4">Login</p>
         <MDBRow>
         <MDBCol md="6">
      <div >
        {this.state.error && <p>{this.state.error}</p>}
        <form onSubmit={this.submitHandler}>
          {this.state.valerrors &&
            this.state.valerrors.email && (
              <p>{this.state.valerrors.email.msg}</p>
            )}
           <MDBInput
            onChange={this.changeHandler}
            label="Registered Email"
            icon="envelope"
            type="email"
            name="email"
            id="email"
          />{" "}
          <br />
          {this.state.valerrors &&
            this.state.valerrors.password && (
              <p>{this.state.valerrors.password.msg}</p>
            )}
          <MDBInput
            onChange={this.changeHandler}
            icon="lock"
            label="Password"
            type="password"
            name="password"
            id="password"
          />{" "}
          <br />
          <button  class="btn btn-info btn-block my-4" type="submit">Submit</button>
        </form>
        <br />


        {t[this.state.currentLanguage].welcome}


      </div>
         </MDBCol>
       </MDBRow>
    </MDBContainer>
    );
  }
}

export default Login;
