import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const File = props => (
    <tr>
        <td>{props.file.file_description}</td>
        <td>{props.file.file_extension}</td>
        <td>{props.file.file_size}</td>
        <td>{props.file.file_duration}</td>
        <td>{props.file.file_dimensions}</td>
        <td>{props.file.file_tag}</td>
        <td>{props.file.file_employeeResponsible}</td>
        <td>{props.file.file_editActive}</td>

        <td>
            <Link to={"/edit/" + props.file._id}>Edit</Link>
            <Link to={"/delete/" + props.file._id}> Delete</Link>
            <Link to={"/pastVersions/"+props.file._id}> Past Versions </Link>
        </td>
    </tr>
)


export default class FilesList extends Component {

    constructor(props) {
        super(props);

        this.onChangeFileSearch = this.onChangeFileSearch.bind(this);

        this.onSubmit = this.onSubmit.bind(this);



        this.state = { files: [],fileOBJ:null,filterString:'', isLogged: true, searchString: "", searchCategory: "" };
        // this.getPosts();
        axios
            .get("http://localhost:4000/api/isLogged")
            .then(res => {
                if (!res.data) {
                    return this.setState({ isLogged: false });
                }
            });
        }

    onChangeFileSearch(e) {
        this.setState({
            file_search: e.target.value
        });
    }


    onSubmit(e) {
        e.preventDefault();

        console.log(`Form submitted:`);
        console.log(`File Description: ${this.state.file_description}`);
        console.log(`File Size: ${this.state.file_size}`);
        console.log(`File Duration: ${this.state.file_duration}`);
        console.log(`File Artist: ${this.state.file_artist}`);
        console.log(`File Tag: ${this.state.file_tag}`);
        console.log(`File Bitrate: ${this.state.file_bitrate}`);
        console.log(`File Codec: ${this.state.file_codec}`);
        console.log(`File Audio Channels: ${this.state.file_audioChannels}`);
        console.log(`File Dimensions: ${this.state.file_dimensions}`);
        console.log(`File Color Profile: ${this.state.file_colorProfile}`);
        console.log(`File Extension: ${this.state.file_extension}`);
        console.log(`Employee Responsible: ${this.state.file_employeeResponsible}`);
        console.log(`Edit Active: ${this.state.file_editActive}`);
        console.log(`Completed: ${this.state.file_completed}`);

        const resultFile = {
            file_description: this.state.file_description,
            file_size: this.state.file_size,
            file_duration: this.state.file_duration,
            file_artist: this.state.file_artist,
            file_bitrate: this.state.file_bitrate,
            file_codec: this.state.file_codec,
            file_tag: this.state.file_tag,
            file_audioChannels: this.state.file_audioChannels,
            file_dimensions: this.state.file_dimensions,
            file_colorProfile: this.state.file_colorProfile,
            file_extension: this.state.file_extension,
            file_employeeResponsible: this.state.file_employeeResponsible,
            file_editActive: this.state.file_editActive,
            file_completed: this.state.file_completed
        }

        axios.post('http://localhost:4000/files/search', resultFile)
            .then(result => {
                if (result.data.errors) {
                    return this.setState(result.data);
                }
                return this.setState({
                    userdata: result.data,
                    errors: null,
                    success: true
                });

            });



        this.setState({
            file_description: '',
            file_size: '',
            file_duration: '',
            file_tag: '',
            file_artist: '',
            file_bitrate: '',
            file_codec: '',
            file_audioChannels: '',
            file_dimensions: '',
            file_colorProfile: '',
            file_extension: '',
            file_employeeResponsible: '',
            file_editActive: '',
            file_completed: false
        })

    }




        componentDidMount() {

        axios.get('http://localhost:4000/files/')
            .then(response => {
                this.setState({ files: response.data });
            })
            .catch(function (error) {
                console.log(error);
            })
        }

    componentDidUpdate() {
        axios.get('http://localhost:4000/files/')
            .then(response => {
                 this.setState({ files: response.data });
            })
            .catch(function (error) {
                console.log(error);
            })
    }

    fileList() {

        return this.state.files.map(function (currentFile, i) {
            return <File file={currentFile} key={i} />;
        });

    }

    // onChangeSearchString = e => {
    //     this.setState({
    //         searchString: e.target.value
    //     })
    // }
    //
    // onChangeSearchCategory = e => {
    //     this.setState({
    //         searchCategory: e.target.value
    //     })
    // }
    //
    // onSearch = e => {
    //     e.preventDefault();
    //     const searchFile = {
    //         searchString: this.state.searchString
    //     }
    //
    //     axios.post('http://localhost:4000/files/search', searchFile)
    //     .then( res =>{
    //         if(res.data){
    //             console.log(res.data);
    //
    //         }else{
    //             console.log("Not Found");
    //         }
    //     })
    //
    //     this.setState({
    //         searchString: ""
    //     })
    // }

    render() {

        return this.state.isLogged ? (

            <div>
                <h3>Files List</h3>
                <table className="table table-striped" style={{ marginTop: 20 }}>
                    <thead>
                        <tr>
                            <th>File Name</th>
                            <th>File Extension</th>
                            <th>File Size (In MB/s)</th>
                            <th>File Duration (In Minutes)</th>
                            <th>File Dimensions (In px)</th>
                            <th>File Tag</th>
                            <th>Employee Responsible</th>
                            <th>Edit Active?</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.fileList()}
                    </tbody>
                </table>

                <div className="form-group">
                    <label>File Tag: </label>
                    <input type="text"
                           className="form-control"
                           value={this.state.file_search}
                           onChange={this.onChangeFileSearch}
                    />
                </div>

                <form>
                    <div className="form-group" >
                        <label>Search String</label>
                        <input type = "text" value = {this.state.searchString} onChange = {this.onChangeSearchString} className = "form-control" />
                    </div>

                    {/*<div className = "form-group">*/}
                    {/*< input className="btn btn-primary" onClick = {this.onSearch} type="submit" value = "Search File" />*/}
                    {/*</div>*/}
                    <div className="form-group">
                        <input type="submit" value="search File" className="btn btn-primary"/>
                    </div>
                </form>
            </div>

        ) : (
                <h3>Please login</h3>
            );
    } 
}
